import SliderChanger from './SliderChanger.js';
import Container from '@mui/material/Container';
import BooleanChanger from './BooleanChanger.js';

function ValueChanger(props){//Add steps and min and max ranges.
    
    const valueChanger = createValueChanger(props);
    return(
        
   
            <Container sx={{border: 'solid red', mb: 2}}>
                <h2>{props.property}</h2>
                {valueChanger}
            </Container>

                
     
        );
}


function createValueChanger(props){

    const sendChange= (newValue) => {
        props.onValueChange([props.property, newValue]);
    }

    let valueChangerComponent;
    switch(typeof props.value){
        case('boolean'):
            valueChangerComponent = <BooleanChanger onValueChange={sendChange} value={props.value}></BooleanChanger>
            break;
        case('number'):
            valueChangerComponent = <SliderChanger onValueChange={sendChange} value={props.value}></SliderChanger>;
            break;

        default:
            valueChangerComponent = <h1>hi</h1>
            // valueChangerComponent = <SliderChanger onValueChange={sendChange} value={props.value}></SliderChanger>;
    }
    return valueChangerComponent;
}

export default ValueChanger;
