import Button from '@mui/material/Button';
import Client from './Client.js';
import {loadYAML} from './YAML.js';
function Controller(props){
    const yamlFile = loadYAML("test.yml");
    const properties = yamlFile.clients['1'];
    
    const changeValue = ([property, value]) => {//Array destructuring. Will take the array of property and value and make it into two variables. 
        properties[property] = value;
    }
    
    const saveChanges = () => {
        fetch('/getData/2').then(x => x.json().then(res => {
            console.log(res.status);
        }))
        console.log(properties);
    }
    return(
        <div>
            <h1>
                Hello this is {props.name}
            </h1>
            <Button variant="contained">Click</Button>
            <Client properties={properties} onValueChange={changeValue}></Client>
            {/* <ValueChanger onValueChange={sendChange} name="temperature" value={10}></ValueChanger> */}
            <Button variant="contained" color="success" onClick={saveChanges}>Save Changes</Button>
        </div>
    )
}


export default Controller;
