import React, { useEffect, useState } from 'react'
import Select from '@mui/material/Select'
import MenuItem from '@mui/material/MenuItem'
function DropdownChanger(props) {
    const [value, setValue] = useState(props.value)
    const handleChange = (event) => {
        console.log(props.value)
        setValue(event.target.value)
        props.onValueChange([props.keyHistory, event.target.value])
    }

    let returnSelect
    if (props.options.differentLabels) {
        returnSelect = (
            <Select disabled={props.isDisabled} size="small" sx={{ mb: 1, minWidth: 5, fontSize: 10 }}           MenuProps={{ PaperProps: { sx: { maxHeight: 100 } } }}
            value={value} onChange={handleChange}>
                {props.options.selections.map((option) => (
                    <MenuItem key={option.value} value={option.value}>
                        {option.label}{' '}
                    </MenuItem>
                ))}
            </Select>
        )
    } else {
        returnSelect = (
            <Select disabled={props.isDisabled} size="small"  sx={{ mb: 1, minWidth: 5, fontSize: 10 }} value={value} onChange={handleChange}>
                {props.options.selections.map((option) => (
                    <MenuItem key={option} value={option} style={{fontSize: 10}}>
                        {option}{' '}
                    </MenuItem>
                ))}
            </Select>
        )
    }
    return returnSelect
}

export default DropdownChanger
