import React, { useState } from 'react';

import Slider from '@mui/material/Slider';

function SliderChanger(props){
    const [value, setValue] = useState(props.value);
    const handleChange = (event, newValue) => {
        setValue(newValue);
    };
    const sendChange = () => {
        props.onValueChange(value);
    }
    return(
    
            
            <Slider valueLabelDisplay="on" value={value} min={props.options.min} max={props.options.max} step={props.options.step} marks={props.options.marks} track={props.options.track} color="Secondary" onChange={handleChange} onChangeCommitted={sendChange}></Slider>

        )

}

export default SliderChanger;
