import React from 'react';
import ValueChanger from "./ValueChanger";
import Grid from '@mui/material/Unstable_Grid2'; // Grid version 2
const xs = {
    'small': 12,
    'normal': 12,
    'large': 12
}
const sm = {
    'small': 4,
    'normal': 6,
    'large': 12
}
const md = {
    'small':3,
    'normal':4,
    'large': 12
}
const xl = {
    'small' : 1,
    'normal': 4,
    'large': 12
}
function Client(props){
    
    let valueChangerList = [];
    const sendChange= (changes) => {
        console.log(changes);
        props.onValueChange([props.clientNumber, changes]);
    }
    
    for(const [property, value] of Object.entries(props.properties)){
        const size = props.propertiesDB[property]['size'] || 'large';
        valueChangerList.push(
            <Grid key={property} xs={xs[size]} sm={sm[size]} md={md[size]} xl={xl[size]} sx={{pr: 1, pb: 1}}>
                <ValueChanger property={property} keyHistory={[]} value={value} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
            </Grid>
        )
    }



    return(
        <div>
            <h2>Client #{props.clientNumber}</h2>
            <Grid container spacing={0} sx={{backgroundColor: '#e3efff', borderRadius: 2}}>
                
                {valueChangerList}
            </Grid>
        </div>


        
    )
}

export default Client;
