import SliderChanger from './SliderChanger.js';
import {Box} from '@mui/material/';
import BooleanChanger from './BooleanChanger.js';
import DropdownChanger from './DropdownChanger.js';
import Section from './Section.js';
import CollapsibleChanger from './CollapsibleChanger.js';
import TextField from './TextChanger.js';

import { Component } from 'react';

function ValueChanger(props){//Add steps and min and max ranges.
    const valueChanger = createValueChanger(props);
    return(
        <Box sx={{background: '#b7eef7', px: 0, textAlign:'center', borderRadius: 3, border: props.hasBorder ? 'solid 1px' : 'none'}}>
            <h2>{props.propertiesDB[props.property].name}</h2>
            {valueChanger}
        </Box>
    );
}


function createValueChanger(props){

    const sendChange = (newValue) => {
        console.log(newValue);
        props.onValueChange(newValue);
    }
    const getOptions = (property) => {
        return props.propertiesDB[property]['options'];//Only place to change this
    }
    let valueChangerComponent;
    const componentType = props.propertiesDB[props.property]['type'];
    const newKeyHistory = [...props.keyHistory, props.property];
    switch(componentType){//THIS WILL BE THE LOOKUP TABLE
        case('boolean'):
            valueChangerComponent = <BooleanChanger onValueChange={sendChange} keyHistory={newKeyHistory} value={props.value}></BooleanChanger>
            break;
        case('slider'):
            valueChangerComponent = <SliderChanger sx={{}} onValueChange={sendChange}  keyHistory={newKeyHistory} value={props.value} options={getOptions(props.property)}></SliderChanger>;
            break;
        case('dropDown'):
            valueChangerComponent = <DropdownChanger onValueChange={sendChange} keyHistory={newKeyHistory} value={props.value} options={getOptions(props.property)}></DropdownChanger>
            break;
        case('section'):
            valueChangerComponent = <Section value={props.value} onValueChange={sendChange} keyHistory={newKeyHistory} property={props.property} propertiesDB={props.propertiesDB}></Section>;
            break;
        case('collapsible'):
            valueChangerComponent = <CollapsibleChanger value={props.value} onValueChange={sendChange} keyHistory={newKeyHistory} property={props.property} propertiesDB={props.propertiesDB}></CollapsibleChanger>
            break;
        case('text'):
            valueChangerComponent = <TextField value={props.value} onValueChange={sendChange} keyHistory={newKeyHistory} property={props.property} propertiesDB={props.propertiesDB}></TextField>

        
    }
    return valueChangerComponent;
}

export default ValueChanger;
