import React, {useState} from 'react';
import Grid from '@mui/material/Unstable_Grid2/Grid2';
import ValueChanger from './ValueChanger';
import Button from '@mui/material/Button';

function ChannelChanger(props){
    const [isDisabled, setIsDisabled] = useState(!props.isActive);
    const [buttonText, setButtonText] = useState(isDisabled ? 'Inactive' : 'Active');
    const [isConfirmed, setIsConfirmed] = useState(false);

    const sendChange = (newValue) => {
        props.onValueChange(newValue);
    }
    const handleChange = () => {
        if(isConfirmed){
            setIsDisabled(!isDisabled);
            setButtonText(isDisabled ? 'Active' : 'Inactive')
            setIsConfirmed(false);
            props.channelUpdate(props.channelNumber);
        }
        else{
            setButtonText("are you sure?")
            setIsConfirmed(true);
        }


    }
    const properties = props.value;
          return(
            <Grid container spacing={1} sx={{borderLeft: 'solid 2px', borderRight: 'solid 2px', px: 1}}>
            <Grid xs={12} md={12}>
                <h3 style={{marginTop: 0, marginBottom: 0}}>Channel {props.channelNumber}</h3>
            </Grid>
            <Grid xs={12} md={6}>
                    <ValueChanger property={'name'} isDisabled={isDisabled} value={properties['name']} keyHistory={props.keyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
                </Grid>
                <Grid xs={12} md={6}>
                    <ValueChanger property={'coupling'} isDisabled={isDisabled} value={properties['coupling']} keyHistory={props.keyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
                </Grid>
                <Grid xs={12} md={6}>
                    <ValueChanger property={'bwLimit'} isDisabled={isDisabled} value={properties['bwLimit']} keyHistory={props.keyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
                </Grid>
                <Grid xs={12} md={6}>
                    <ValueChanger property={'probe'} isDisabled={isDisabled} value={properties['probe']} keyHistory={props.keyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
                </Grid>
                <Grid xs={12} md={6}>
                    <ValueChanger property={'tdiv'} isDisabled={isDisabled} value={properties['tdiv']} keyHistory={props.keyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
                </Grid>
                <Grid xs={12} md={6}>
                    <ValueChanger property={'timeOffset'} isDisabled={isDisabled} value={properties['timeOffset'] ? properties['timeOffset'] : properties['tOffset']} keyHistory={props.keyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
                </Grid>
                <Grid xs={12} md={6}>
                    <ValueChanger property={'vdiv'} isDisabled={isDisabled} value={properties['vdiv']} keyHistory={props.keyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
                </Grid>
                <Grid xs={12} md={6}>
                    <ValueChanger property={'voltageOffset'} isDisabled={isDisabled} value={properties['voltageOffset'] ? properties['voltageOffset'] : properties['vOffset'] ? properties['vOffset'] : 0} keyHistory={props.keyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
                </Grid>
            <Grid xs={12}md={12}>
                <Button variant="contained" sx={{background: isConfirmed ? 'orange' : isDisabled ? 'red' : 'green', width: '100%', height: 50}} onClick={handleChange}>
                    <h3>
                        {buttonText}
                    </h3>
                </Button>
            </Grid>
        </Grid>
        )

    
}


export default ChannelChanger;
