import Button from '@mui/material/Button';
import Client from './Client.js';
import React, { useState } from 'react';

import {loadYAML} from './YAML.js';
function Controller(props){
    const [clients, setClients] = useState([]);
    const yamlFile = loadYAML("test.yml");
    const [properties, setProperties] = useState(undefined);//
    
    const changeValue = ([clientNumber, property, value]) => {//Array destructuring. Will take the array of property and value and make it into two variables. 
        clients[clientNumber][property] = value;
    }
    
    const getClient = async (clientNumber) => {
        let req = await fetch(`/getData/`, {method: 'GET'});
        console.log(req);
        let json = await req.json();
        console.log(json);
        //PUT THIS INTO ANOTHER FUNCTION
        let tempClients = [];
        for(const [clientNumber, clientData] of Object.entries(json)){
            tempClients.push(<Client clientNumber={clientNumber} properties={clientData} onValueChange={changeValue}></Client>);
        }
        console.log(tempClients);
        setClients(tempClients);

        //setProperties(json);
        //Set properties to json. Should update state. 
    }

    const saveChanges = async (clientNumber) => {
       console.log("Saving: ");
       console.log(properties);
       let req = await fetch(`/getData/`, 
       {method:'POST', headers:{'Content-Type':'application/json'}, body: JSON.stringify(properties)}
       );
       let json = await req.json();
       console.log(json);
       
    }


    if(clients){
        return(
            <div>
                <h1>
                    Hello this is {props.name}
                </h1>
                {clients}
                {/* <Client properties={properties} onValueChange={changeValue}></Client> */}
                {/* <ValueChanger onValueChange={sendChange} name="temperature" value={10}></ValueChanger> */}
                <Button variant="contained" color="success" onClick={() => saveChanges(1)}>Save Changes</Button>
            </div>
        )
    }
    else{
        return(
            <div>
                <Button variant="contained" color="success" onClick={() => getClient(1)}>Get Data</Button>

                <h1>Waiting for properties</h1>
            </div>
            
        )
    }
}


export default Controller;
