import SliderChanger from './SliderChanger.js';
import Container from '@mui/material/Container';
import BooleanChanger from './BooleanChanger.js';
import DropdownChanger from './DropdownChanger.js';
import Section from './Section.js';
import CollapsibleChanger from './CollapsibleChanger.js';

import { Component } from 'react';

function ValueChanger(props){//Add steps and min and max ranges.
    
    const valueChanger = createValueChanger(props);

    return(
        
   
            <Container sx={{border: 'solid red', mb: 2}}>
                <h2>{props.property}</h2>
                {valueChanger}
            </Container>

                
     
        );
}


function createValueChanger(props){

    const sendChange = (newValue) => {
        console.log("Value Changer recieved new value: " + newValue);
        props.onValueChange([props.property, newValue]);
    }
    let valueChangerComponent;
    let componentType = props.propertiesDB['property']['type'];
    switch(componentType){//THIS WILL BE THE LOOKUP TABLE
        case('boolean'):
            valueChangerComponent = <BooleanChanger onValueChange={sendChange} value={props.value} options={props.properties.options}></BooleanChanger>
            break;
        case('slider'):
            valueChangerComponent = <SliderChanger onValueChange={sendChange} value={props.value} options={props.properties.options}></SliderChanger>;
            break;
        case('dropDown'):
            valueChangerComponent = <DropdownChanger onValueChange={sendChange} value={props.value} options={props.properties.options}></DropdownChanger>
            break;
        case('section'):
            valueChangerComponent = <Section property={props.property} value={props.value}></Section>;
            break;
        case('collapsible'):
            valueChangerComponent = <CollapsibleChanger value={props.value}></CollapsibleChanger>
            break;
        
    }
    return valueChangerComponent;
}

export default ValueChanger;
