import Button from '@mui/material/Button';
import Client from './Client.js';
import React, { useState } from 'react';

import {loadYAML} from './YAML.js';
let mainConfig;

function Controller(props){
    const [clients, setClients] = useState([]);

    const yamlFile = loadYAML("test.yml");
    console.log("Controller rerender");
    
    const changeValue = ([clientNumber, [keyHistory, newValue]]) => {//Array destructuring. Will take the array of property and value and make it into two variables. 
       //clients[clientNumber][property] = value;
        console.log(clientNumber);
        console.log(keyHistory);
        console.log(newValue);
        let client = mainConfig[clientNumber];
        console.log(client);
        for(let i = 0; i < keyHistory.length - 1; i++){//Go through keyhistory (except last one). Now client is pointing to the last object which is the {proprety: value}.
            client = client[keyHistory[i]];
        }
        client[keyHistory[keyHistory.length - 1]] = newValue;
        console.log(client);
       console.log(mainConfig);
    }
    const generateClients = (json, propertiesDB) => {
        let tempArray = [];
        for(const [clientNumber, clientData] of Object.entries(json)){
            tempArray.push(<Client sx={{ml: 0}}key={clientNumber} clientNumber={clientNumber} properties={clientData} propertiesDB={propertiesDB} onValueChange={changeValue}></Client>);
        }
        return tempArray;
    }

    const getClient = async (clientNumber) => {
        const req = await fetch(`/getData/`, {method: 'GET'});
        const json = await req.json();
        const propertiesDB = json['propertiesDB'];
        mainConfig = json['clients'];
        console.log(req);

        let tempClients = generateClients(json['clients'], propertiesDB);//Properties DB seems to be passes by reference
        setClients(tempClients);

        //setProperties(json);
        //Set properties to json. Should update state. 
    }

    const saveChanges = async (clientNumber) => {
       console.log("Saving: ");
       
       let req = await fetch(`/getData/`, 
       {method:'POST', headers:{'Content-Type':'application/json'}, body: JSON.stringify(mainConfig)}
       );
       let json = await req.json();//Check for errors
       console.log(json);
       
    }


    if(clients.length > 0){
        return(
            <div>
                <h1>
                    Hello this is {props.name} &#8486;
                </h1>
                {clients}
                {/* <Client properties={properties} onValueChange={changeValue}></Client> */}
                {/* <ValueChanger onValueChange={sendChange} name="temperature" value={10}></ValueChanger> */}
                <Button variant="contained" color="success" onClick={() => saveChanges(1)}>Save Changes</Button>
            </div>
        )
    }
    else{
        return(
            <div>
                <Button variant="contained" color="success" onClick={() => getClient(1)}>Get Data</Button>
                <h1>Waiting for clients...</h1>
            </div>
            
        )
    }
}


export default Controller;
