import React, {useState} from 'react';
import Grid from '@mui/material/Unstable_Grid2/Grid2';
import Box from '@mui/material/Box';
import {Accordion, AccordionSummary, AccordionDetails} from '@mui/material';

import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import ValueChanger from './ValueChanger';
import ChannelChanger from './ChannelChanger.js';
const defaultChannel = {
    "bwLimit": false,
    "coupling": "A1M",
    "id": "ch1",
    "name": "IFxxx_CO2_refINTERSCOPE1",
    "probe": 10,
    "tdiv": "2MS",
    "timeOffset": "12MS",
    "vdiv": "1v",
    "voltageOffset": 0
};
function ScopeChanger(props){
    const scopeIndex = props.scopeIndex;
    const properties = props.value;
   
    let channelProperties = {...properties['channelsConfigSettings']}; 
    let activeChannels = [...properties['activeChannels']];
    const newKeyHistory = [...props.keyHistory, scopeIndex];//CHANGE ONE
    let channels = [];
    const [currentType, setCurrentType] = useState(properties['type']);
    const [name, setName] = useState(properties.name);
    

    const sendChange = (newValue) => {
        props.onValueChange(newValue);
    }
    const changeScopeType = (newType) => {
        setCurrentType(newType);
        sendChange(newType);
    }
    const channelUpdate = (channel) => {
        if(!activeChannels.includes(channel)){
            console.log("Channel not enabled currently");
            activeChannels.push(channel);
            channelProperties[channel] = {...defaultChannel};
        }
        else{
            delete channelProperties[channel];
            activeChannels.splice(activeChannels.indexOf(channel), 1);
        }
        sendChange([[...props.keyHistory, scopeIndex, 'activeChannels'], activeChannels]);
        sendChange([[...props.keyHistory, scopeIndex, 'channelsConfigSettings'], channelProperties]);
    }

    const nameUpdates = (newName) => {
        setName(newName);
        sendChange(newName);
    }

    for(let channelIndex = 1; channelIndex <= 8; channelIndex++){
        if(activeChannels.includes(channelIndex)){
            channels.push(
                // <Grid key={channelIndex} md={4}>
                <ImageListItem key={channelIndex}>
                    <ChannelChanger value={properties['channelsConfigSettings'][channelIndex]} channelNumber={channelIndex} isActive={true} keyHistory={[...newKeyHistory, 'channelsConfigSettings', channelIndex]} propertiesDB={props.propertiesDB} onValueChange={sendChange} channelUpdate={channelUpdate}></ChannelChanger>
                </ImageListItem>
                // </Grid>      
            )    
        }
        else{
            channels.push(
                // <Grid key={channelIndex} md={4}>
                <ImageListItem key={channelIndex}>
                    <ChannelChanger value={defaultChannel} channelNumber={channelIndex} isActive={false} keyHistory={[...newKeyHistory, 'channelsConfigSettings', channelIndex]} propertiesDB={props.propertiesDB} onValueChange={sendChange} channelUpdate={channelUpdate}></ChannelChanger>
                </ImageListItem>
                // </Grid>   
            )    
        }
        
    }

    return(
        <Accordion>
            <AccordionSummary>
                {properties.name}
            </AccordionSummary>
            <AccordionDetails>
            <Grid container columns={16} spacing={1} sx={{px: 1}} >
            {/* ROW 1 */}
            <Grid md={3}>
                <ValueChanger property={'connectionString'} value={properties['connectionString']} keyHistory={newKeyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
            </Grid>
            <Grid md={3}>
                <ValueChanger property={'waveFormat'} isDisabled={properties['waveFormat'] ? false : true}value={properties['waveFormat'] ? properties['waveFormat'] : 'BYTE'} keyHistory={newKeyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
            </Grid>
            <Grid md={4}>
                <ValueChanger property={'name'} value={properties['name']} keyHistory={newKeyHistory} propertiesDB={props.propertiesDB} onValueChange={nameUpdates}></ValueChanger>
            </Grid>
            <Grid md={3}>
                <ValueChanger property={'filePath'} value={properties['filePath']} keyHistory={newKeyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
            </Grid>
            <Grid md={3}>
                <ValueChanger property={'fileFormat'} value={properties['fileFormat']} keyHistory={newKeyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
            </Grid>
            {/* ROW 1 */}
            {/* ROW 2 */}
            <Grid md={2}>
                <ValueChanger property={'type'} value={properties['type']} keyHistory={newKeyHistory} propertiesDB={props.propertiesDB} onValueChange={changeScopeType}></ValueChanger>
            </Grid>
            <Grid md={2}>
                <ValueChanger property={'triggerType'} value={properties['triggerType']} keyHistory={newKeyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
            </Grid>
            <Grid md={2}>
                <ValueChanger property={'memSize'} value={properties['memSize']} keyHistory={newKeyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
            </Grid>
            <Grid md={2}>
                <ValueChanger property={'tdiv'} isDisabled={properties['tdiv'] ? false : true} value={properties['tdiv'] ? properties['tdiv'] : '2MS'} keyHistory={newKeyHistory}propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
            </Grid>
            <Grid md={2}>
                <ValueChanger property={'samplingRate'} isDisabled={properties['samplingRate'] ? false : true} value={properties['samplingRate'] ? properties['samplingRate'] : 0} keyHistory={newKeyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
            </Grid>
            <Grid md={2}>
                <ValueChanger property={'timeOffset'} isDisabled={properties['timeOffset'] ? false : true} value={properties['timeOffset'] ? properties['timeOffset'] : 0} keyHistory={newKeyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
            </Grid>
            <Grid md={2}>
                <ValueChanger property={'triggerLevel'} isDisabled={properties['triggerLevel'] ? false : true} value={properties['triggerLevel'] ? properties['triggerLevel'] : '2.5V'} keyHistory={newKeyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
            </Grid>
            <Grid md={2}>
                <ValueChanger property={'triggerDelay'} value={properties['triggerDelay']} keyHistory={newKeyHistory} propertiesDB={props.propertiesDB} onValueChange={sendChange}></ValueChanger>
            </Grid>
            {/* ROW 2 */}

            {/* ROW 3 */}
            
            <Grid md={16}>
            <ImageList
            sx={{
                gridAutoFlow: "column",
                gridTemplateColumns: "repeat(auto-fill,minmax(25%,1fr)) !important",
                gridAutoColumns: "minmax(25%, 1fr)",
                overflowY: 'hidden',
                
                
            }}
            >
                {channels}
            </ImageList>
                

               

            </Grid>
            
 

        
            {/* ROW 3 */}


        </Grid>
            </AccordionDetails>
        </Accordion>

    )
}

export default ScopeChanger;
