import React, { useState } from 'react';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
function getLabel(value, options){
    let label;
    for(let option of options){
        if(option['value'] == value){
            label = option['label'];
            break;  
        }
    }
    return label;
}
function DropdownChanger(props){
    const [value, setValue] = useState(props.value);
    const [optionsArray, setOptionsArray] = useState([]);
    let tempOptionsArray = [];
    console.log(getLabel(value ,props.options));
    return(

   
         <Select value={value}>
            <MenuItem value={value}>{getLabel(value, props.options)}</MenuItem>
         </Select>
    )
}

export default DropdownChanger;
