import SliderChanger from './SliderChanger.js';
import Container from '@mui/material/Container';
import BooleanChanger from './BooleanChanger.js';
import DropdownChanger from './DropdownChanger.js';
import Section from './Section.js';
import CollapsibleChanger from './CollapsibleChanger.js';

import { Component } from 'react';

function ValueChanger(props){//Add steps and min and max ranges.
    const valueChanger = createValueChanger(props);
   
    return(
        
   
            <Container sx={{border: 'solid red', width: 200}}>
                <h2>{props.property}</h2>
                {valueChanger}
            </Container>

                
     
        );
}


function createValueChanger(props){

    const sendChange = (newValue) => {
        console.log(newValue);
        props.onValueChange(newValue);
    }
    const getOptions = (property) => {
        return props.propertiesDB[property]['options'];//Only place to change this
    }
    let valueChangerComponent;
    const componentType = props.propertiesDB[props.property]['type'];
    switch(componentType){//THIS WILL BE THE LOOKUP TABLE
        case('boolean'):
            valueChangerComponent = <BooleanChanger onValueChange={sendChange} keyHistory={[...props.keyHistory, props.property]} value={props.value}></BooleanChanger>
            break;
        case('slider'):
            valueChangerComponent = <SliderChanger onValueChange={sendChange}  keyHistory={[...props.keyHistory, props.property]} value={props.value} options={getOptions(props.property)}></SliderChanger>;
            break;
        case('dropDown'):
            valueChangerComponent = <DropdownChanger onValueChange={sendChange} keyHistory={[...props.keyHistory, props.property]} value={props.value} options={getOptions(props.property)}></DropdownChanger>
            break;
        case('section'):
            valueChangerComponent = <Section value={props.value} onValueChange={sendChange} keyHistory={[...props.keyHistory, props.property]} property={props.property} propertiesDB={props.propertiesDB}></Section>;
            break;
        case('collapsible'):
            valueChangerComponent = <CollapsibleChanger value={props.value} onValueChange={sendChange} keyHistory={[...props.keyHistory]} property={props.property} propertiesDB={props.propertiesDB}></CollapsibleChanger>
            break;
        
    }
    return valueChangerComponent;
}

export default ValueChanger;
